import react, {useState} from 'react';
import axios from 'axios';

function App() {

	const [symbol, setSymbol] = useState('');
	const [date, setDate] = useState('');
	const [stats, setStats] = useState('');

	const handleSubmit = e => {
		e.preventDefault();

		const data = { symbol, date };
		axios.post("https://localhost:5000/api/fetchStockData", data)
		.then(res => setStats(res.data))
		.catch(err => console.error("Error fetching data: ", err));
	}

	return (
		<div>
			<h1>Stock Trade Statistics</h1>
			<form onSubmit={handleSubmit}>
				<label>Stock Symbol:</label>
				<input type="text" id="symbol" value={symbol} onChange={sym => setSymbol(sym.target.value)} required/>
				<label>Date:</label>
				<input type="date" id="date" value={date} onChange={dt => setDate(dt.target.value)} required/>
				<button type="submit">Get Statistics</button>
			</form>

			{stats && (
				<div id="stats">
					<h2>
						Trade Statistics for Symbol {stats.symbol} on {stats.day}
					</h2>
					<ul>
						<li>Open: {stats.open}</li>
						<li>High: {stats.high}</li>
						<li>Low: {stats.low}</li>
						<li>Close: {stats.close}</li>
						<li>Volume: {stats.volume}</li>
					</ul>
				</div>
			)}
		</div>
	);
}

export default App;
