import {useState} from 'react';
import axios from 'axios';

function App() {

	const [symbol, setSymbol] = useState('');
	const [date, setDate] = useState('');
	const [stats, setStats] = useState({});

	const handleSubmit = e => {
		e.preventDefault();

		const data = { symbol, date };
		axios.post("http://localhost:5000/api/fetchStockData/", data)
		.then(res => {
			console.log(res.data);setStats(res.data);
		} )
		.catch(err => console.error("Error fetching data: ", err));
	}

	return (
		<div>
			<h1>Stock Trade Statistics</h1>
			<form onSubmit={handleSubmit}>
				<label>Stock Symbol:</label>
				<input type="text" id="symbol" value={symbol} onChange={sym => setSymbol(sym.target.value)} required/>
				<label>Date:</label>
				<input type="date" id="date" value={date} onChange={dt => setDate(dt.target.value)} required/>
				<button type="submit">Get Statistics</button>
			</form>

			{stats && (
				<div id="stats">
					<div>{stats}</div>
					<h2>
						Trade Statistics for Symbol {stats.data.symbol} on {stats.data.from}
					</h2>
					<ul>
						<li>Open: {stats.data.open}</li>
						<li>High: {stats.data.high}</li>
						<li>Low: {stats.data.low}</li>
						<li>Close: {stats.data.close}</li>
						<li>Volume: {stats.data.volume}</li>
					</ul>
				</div>
			)}
		</div>
	);
}

export default App;
